let selects=document.querySelectorAll(".select");for(let e=0;e<selects.length;e++)selects[e].addEventListener("click",(()=>{selects[e].value="addElement"})),selects[e].addEventListener("change",(t=>{let l=document.createElement("li");l.insertAdjacentHTML("beforeend",`<div class="list-item-simple">\n                <div class="list-item-simple__text">${selects[e].options[selects[e].selectedIndex].text}</div>\n                <div class="list-item-simple__cancel-btn"><i class="ri-close-line ri-2x"></i></div>\n            </div>`);let s=l.querySelector(".list-item-simple__cancel-btn");s.addEventListener("click",(()=>{s.closest("li").remove()})),document.querySelector(".ol-edit-project").append(l)}));let switchers=document.querySelectorAll(".switcher");for(let e=0;e<switchers.length;e++){let t=switchers[e].querySelectorAll(".switcher__item"),l=switchers[e].querySelector(".switcher__item_selected");for(let e=0;e<t.length;e++)t[e].addEventListener("click",(()=>{l&&l.classList.remove("switcher__item_selected"),l=t[e],l.classList.add("switcher__item_selected")}))}